  o.g = o.g + (o.g - rgbAvg) * saturation;
  o.b = o.b + (o.b - rgbAvg) * saturation;
  if(TotalRGB == 1){
    o.r = (o.r + o.g + o.b);
    o.g = o.b = (o.b + o.g)/2.0;
  }
  else if(TotalRGB == 2){
    o.g = (o.r + o.g + o.b);
    o.r = o.b = (o.b + o.r)/2.0;
  }
  else if(TotalRGB == 3){
    o.b = (o.r + o.g + o.b);
    o.r = o.g = (o.r + o.g)/2.0;
  }
  if(HighRed != 0.0){
    float r = o.r;
    float g = o.g;
    float b = o.b;
    o.r = (g+b)*HighRed/2.0;
    o.b = r/HighRed;
    o.g = r/HighRed;
  }
  if(contrast!=0.0){
    float pct = (o.r + o.g + o.b)/3.0;
    float x = 2.0*pct-1.0;
    float npct = (0.5*sign(x)*pow(abs(x),1.0-contrast)+0.5)/pct;
    o.r = o.r * npct;
    o.g = o.g * npct;
    o.b = o.b * npct;
  }
  if(isMedal == 1){
    float rgb = (o.r + o.g + o.b)/3.0;
    float c = 0.3;
    float x = 2.0*rgb-1.0;
    float nrgb = (0.5*sign(x)*pow(abs(x),1.0-c)+0.5);
    o.r = nrgb * 2.3;
    o.g = nrgb * 1.2;
    o.b = 0.0;
  }
  #if USE_CAUSTIC
    vec3 causticColor = causticParams2.yzw;
    o.rgb += causticColor * caustic();
  #endif
  ALPHA_TEST(o);
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

